CREATE TABLE page (id BIGINT AUTO_INCREMENT, title VARCHAR(100) DEFAULT '' NOT NULL, body LONGTEXT DEFAULT '' NOT NULL, category VARCHAR(32) DEFAULT '' NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, slug VARCHAR(255), UNIQUE INDEX page_sluggable_idx (slug), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE vote_ita (id BIGINT AUTO_INCREMENT, title VARCHAR(100) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE vote_shop (id BIGINT AUTO_INCREMENT, ita_id BIGINT DEFAULT 0 NOT NULL, url VARCHAR(255) DEFAULT NULL, votes BIGINT DEFAULT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE user (id BIGINT AUTO_INCREMENT, user_name VARCHAR(20) DEFAULT '' NOT NULL, show_name VARCHAR(20) DEFAULT '' NOT NULL, password VARCHAR(20) DEFAULT '' NOT NULL, address VARCHAR(200), email VARCHAR(100), twitter BIGINT, mixi BIGINT, facebook BIGINT, google BIGINT, last_login DATETIME, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
